import { useState } from "react";
import Button from '@mui/material/Button';
import toast from "react-hot-toast";
import blake from 'blakejs';

// Función para convertir una cadena a hexadecimal sin usar Buffer
const stringToHex = (str) => {
    return str.split('').map(c => c.charCodeAt(0).toString(16)).join('');
};

// Función para convertir un array de bytes a hexadecimal
const toHex = (byteArray) => {
    return Array.from(byteArray, (byte) => {
        return ('0' + (byte & 0xFF).toString(16)).slice(-2);
    }).join('');
};

// Implementación de Rabin
const generateRabinKeys = () => {
    const p = 61;
    const q = 53;
    const n = p * q;
    return { p, q, n };
};

const rabinEncrypt = (message, n) => {
    // Convertir el mensaje a hexadecimal y luego a un entero
    const hexMessage = stringToHex(message);
    const m = parseInt(hexMessage, 16); // Convertir el mensaje a entero usando hexadecimal
    const c = (m ** 2) % parseInt(n); // Cifrar
    return c.toString();
};

const rabinDecrypt = (cipher, p, q) => {
    const n = parseInt(p * q);
    const c = parseInt(cipher);
    const mp = modSqrt(c, p);
    const mq = modSqrt(c, q);
    const [r1, r2] = chineseRemainderTheorem(mp, mq, p, q, n);
    return [r1, r2];
};

const modSqrt = (a, p) => {
    return Math.pow(a, (p + 1) / 4) % p;
};

const chineseRemainderTheorem = (mp, mq, p, q, n) => {
    const yp = q * modInverse(q, p);
    const yq = p * modInverse(p, q);
    const r1 = (mp * yp + mq * yq) % n;
    const r2 = (mp * yp - mq * yq) % n;
    return [r1, r2];
};

const modInverse = (a, m) => {
    const [g, x] = extendedGcd(a, m);
    if (g !== 1) throw new Error('No existe inverso modular');
    return x % m;
};

const extendedGcd = (a, b) => {
    if (b === 0) return [a, 1, 0];
    const [g, x1, y1] = extendedGcd(b, a % b);
    return [g, y1, x1 - Math.floor(a / b) * y1];
};

// Implementación de RC6 en JavaScript
const w = 32; // Tamaño de palabra (32 bits)
const r = 20; // Número de rondas recomendado
const lgw = Math.log2(w); // Log base 2 del tamaño de palabra

const mod = 2 ** w; // Módulo para operaciones de 32 bits

// Generación de claves para RC6
const generateKeySchedule = (K) => {
    const P32 = 0xB7E15163;
    const Q32 = 0x9E3779B9;

    const t = 2 * (r + 2);
    const S = new Array(t);
    S[0] = P32;
    for (let i = 1; i < t; i++) {
        S[i] = (S[i - 1] + Q32) % mod;
    }

    const L = [];
    for (let i = 0; i < K.length / 4; i++) {
        L[i] = K.readUInt32LE(i * 4);
    }

    let A = 0, B = 0;
    let i = 0, j = 0;
    const v = 3 * Math.max(L.length, t);
    for (let s = 0; s < v; s++) {
        A = S[i] = rol((S[i] + A + B) % mod, 3);
        B = L[j] = rol((L[j] + A + B) % mod, (A + B) % w);
        i = (i + 1) % t;
        j = (j + 1) % L.length;
    }

    return S;
};

// Rotación a la izquierda
const rol = (x, n) => {
    return ((x << n) | (x >>> (w - n))) % mod;
};

// Rotación a la derecha
const ror = (x, n) => {
    return ((x >>> n) | (x << (w - n))) % mod;
};

// Cifrado RC6
const rc6Encrypt = (plaintext, S) => {
    let A = plaintext.readUInt32LE(0);
    let B = plaintext.readUInt32LE(4);
    let C = plaintext.readUInt32LE(8);
    let D = plaintext.readUInt32LE(12);

    B = (B + S[0]) % mod;
    D = (D + S[1]) % mod;

    for (let i = 1; i <= r; i++) {
        const t = rol((B * (2 * B + 1)) % mod, lgw);
        const u = rol((D * (2 * D + 1)) % mod, lgw);
        A = (rol(A ^ t, u) + S[2 * i]) % mod;
        C = (rol(C ^ u, t) + S[2 * i + 1]) % mod;

        [A, B, C, D] = [B, C, D, A];
    }

    A = (A + S[2 * r + 2]) % mod;
    C = (C + S[2 * r + 3]) % mod;

    const ciphertext = Buffer.alloc(16);
    ciphertext.writeUInt32LE(A, 0);
    ciphertext.writeUInt32LE(B, 4);
    ciphertext.writeUInt32LE(C, 8);
    ciphertext.writeUInt32LE(D, 12);
    return ciphertext;
};

// Descifrado RC6
const rc6Decrypt = (ciphertext, S) => {
    let A = ciphertext.readUInt32LE(0);
    let B = ciphertext.readUInt32LE(4);
    let C = ciphertext.readUInt32LE(8);
    let D = ciphertext.readUInt32LE(12);

    C = (C - S[2 * r + 3]) % mod;
    A = (A - S[2 * r + 2]) % mod;

    for (let i = r; i >= 1; i--) {
        [A, B, C, D] = [D, A, B, C];

        const t = rol((B * (2 * B + 1)) % mod, lgw);
        const u = rol((D * (2 * D + 1)) % mod, lgw);
        C = ror((C - S[2 * i + 1]) % mod, t) ^ u;
        A = ror((A - S[2 * i]) % mod, u) ^ t;
    }

    D = (D - S[1]) % mod;
    B = (B - S[0]) % mod;

    const plaintext = Buffer.alloc(16);
    plaintext.writeUInt32LE(A, 0);
    plaintext.writeUInt32LE(B, 4);
    plaintext.writeUInt32LE(C, 8);
    plaintext.writeUInt32LE(D, 12);
    return plaintext;
};

// Función de Hash usando Blake2
const hashBlake2 = (str) => {
    const inputBytes = new TextEncoder().encode(str); // Convertir la cadena en bytes
    const hash = blake.blake2b(inputBytes); // Generar el hash usando blakejs
    return toHex(hash); // Convertir el resultado en formato hexadecimal
};

const CifradoFormulario = () => {
    const [name, setName] = useState('');
    const [address, setAddress] = useState('');
    const [password, setPassword] = useState('');
    const [symmetricKey, setSymmetricKey] = useState('');
    const [publicKey, setPublicKey] = useState('');
    const [privateKey, setPrivateKey] = useState('');
    const [outputMessage, setOutputMessage] = useState('');

    const handleEncryption = () => {
        if (!name.trim() || !address.trim() || !password.trim()) {
            toast.error("Por favor ingrese todos los campos");
            return;
        }
        if (!symmetricKey.trim() || !publicKey.trim()) {
            toast.error("Por favor ingrese las claves");
            return;
        }

        // Convertir la clave simétrica en un buffer
        const keyBuffer = Buffer.alloc(16);
        keyBuffer.write(symmetricKey, 'utf8');
        const S = generateKeySchedule(keyBuffer);

        // Convertir nombre y dirección a buffers
        const nameBuffer = Buffer.alloc(16);
        nameBuffer.write(name, 'utf8');
        const addressBuffer = Buffer.alloc(16);
        addressBuffer.write(address, 'utf8');

        // Cifrar usando RC6
        const encryptedName = rc6Encrypt(nameBuffer, S).toString('hex');
        const encryptedAddress = rc6Encrypt(addressBuffer, S).toString('hex');
        const encryptedPassword = rabinEncrypt(password, publicKey);
        const hashedPassword = hashBlake2(password);

        setOutputMessage(
            `Nombre cifrado (RC6): ${encryptedName}\n` +
            `Dirección cifrada (RC6): ${encryptedAddress}\n` +
            `Contraseña cifrada (Rabin): ${encryptedPassword}\n` +
            `Contraseña hashed (Blake2): ${hashedPassword}`
        );
    };

    const handleDecryption = () => {
        if (!symmetricKey.trim() || !privateKey.trim()) {
            toast.error("Por favor ingrese las claves");
            return;
        }

        try {
            // Convertir la clave simétrica en un buffer
            const keyBuffer = Buffer.alloc(16);
            keyBuffer.write(symmetricKey, 'utf8');
            const S = generateKeySchedule(keyBuffer);

            // Convertir el mensaje cifrado de hex a buffer y descifrar con RC6
            const nameBuffer = Buffer.from(outputMessage.name, 'hex');
            const addressBuffer = Buffer.from(outputMessage.address, 'hex');
            const decryptedName = rc6Decrypt(nameBuffer, S).toString('utf8');
            const decryptedAddress = rc6Decrypt(addressBuffer, S).toString('utf8');
            const decryptedPassword = rabinDecrypt(outputMessage.password, privateKey);

            setOutputMessage(
                `Nombre descifrado: ${decryptedName}\n` +
                `Dirección descifrada: ${decryptedAddress}\n` +
                `Contraseña descifrada: ${decryptedPassword}`
            );
        } catch (error) {
            toast.error("Error al descifrar los datos");
        }
    };

    return (
        <div style={{ padding: "20px", textAlign: "center" }}>
            <h1>Formulario de Cifrado y Hash</h1>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Nombre"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Dirección"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="password"
                    placeholder="Contraseña"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Clave Simétrica (RC6)"
                    value={symmetricKey}
                    onChange={(e) => setSymmetricKey(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Clave Pública (Rabin)"
                    value={publicKey}
                    onChange={(e) => setPublicKey(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Clave Privada (Rabin)"
                    value={privateKey}
                    onChange={(e) => setPrivateKey(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <Button
                    onClick={handleEncryption}
                    variant="contained"
                    color="primary"
                    style={{ marginRight: "10px" }}
                >
                    Cifrar Datos
                </Button>
                <Button
                    onClick={handleDecryption}
                    variant="contained"
                    color="secondary"
                >
                    Descifrar Datos
                </Button>
            </div>
            <div>
                {outputMessage && (
                    <textarea
                        readOnly
                        value={outputMessage}
                        style={{
                            width: "300px",
                            height: "200px",
                            padding: "10px",
                            fontSize: "16px",
                            resize: "none",
                            border: "1px solid #ccc",
                            borderRadius: "4px",
                            marginBottom: "10px",
                        }}
                    />
                )}
            </div>
        </div>
    );
};

export { CifradoFormulario };
