import { useState } from "react";
import Button from '@mui/material/Button';
import toast from "react-hot-toast";
import blake from 'blakejs';

// Función para convertir una cadena a hexadecimal
const toHex = (byteArray) => {
    return Array.from(byteArray, (byte) => {
        return ('0' + (byte & 0xFF).toString(16)).slice(-2);
    }).join('');
};

// Función para ajustar la clave a un tamaño de 16 bytes (128 bits) si es necesario
const adjustKeyLength = (key) => {
    const encoder = new TextEncoder();
    let keyBytes = encoder.encode(key);

    // Si la clave es menor que 16 bytes, la rellenamos con ceros
    if (keyBytes.length < 16) {
        const paddedKey = new Uint8Array(16);
        paddedKey.set(keyBytes);
        keyBytes = paddedKey;
    }

    // Si la clave es mayor que 16 bytes, la truncamos
    if (keyBytes.length > 16) {
        keyBytes = keyBytes.slice(0, 16);
    }

    return keyBytes;
};

// Implementación de RC6 en JavaScript usando DataView
const w = 32; // Tamaño de palabra (32 bits)
const r = 20; // Número de rondas recomendado
const lgw = Math.log2(w); // Log base 2 del tamaño de palabra
const mod = 2 ** w; // Módulo para operaciones de 32 bits

// Generación de claves para RC6
const generateKeySchedule = (K) => {
    const P32 = 0xB7E15163;
    const Q32 = 0x9E3779B9;

    const t = 2 * (r + 2);
    const S = new Array(t);
    S[0] = P32;
    for (let i = 1; i < t; i++) {
        S[i] = (S[i - 1] + Q32) % mod;
    }

    // Usamos DataView para leer enteros de 32 bits
    const L = [];
    const KView = new DataView(K.buffer);
    for (let i = 0; i < K.byteLength / 4; i++) {
        L[i] = KView.getUint32(i * 4, true); // true para little-endian
    }

    let A = 0, B = 0;
    let i = 0, j = 0;
    const v = 3 * Math.max(L.length, t);
    for (let s = 0; s < v; s++) {
        A = S[i] = rol((S[i] + A + B) % mod, 3);
        B = L[j] = rol((L[j] + A + B) % mod, (A + B) % w);
        i = (i + 1) % t;
        j = (j + 1) % L.length;
    }

    return S;
};

// Rotación a la izquierda
const rol = (x, n) => {
    return ((x << n) | (x >>> (w - n))) % mod;
};

// Rotación a la derecha (ror)
const ror = (x, n) => {
    return ((x >>> n) | (x << (w - n))) % mod;
};

// Cifrado RC6
const rc6Encrypt = (plaintext, S) => {
    const encoder = new TextEncoder();
    const plaintextBytes = encoder.encode(plaintext.padEnd(16, '\0')); // Asegurar que el texto tenga 16 bytes
    let A = new DataView(plaintextBytes.buffer).getUint32(0);
    let B = new DataView(plaintextBytes.buffer).getUint32(4);
    let C = new DataView(plaintextBytes.buffer).getUint32(8);
    let D = new DataView(plaintextBytes.buffer).getUint32(12);

    B = (B + S[0]) % mod;
    D = (D + S[1]) % mod;

    for (let i = 1; i <= r; i++) {
        const t = rol((B * (2 * B + 1)) % mod, lgw);
        const u = rol((D * (2 * D + 1)) % mod, lgw);
        A = (rol(A ^ t, u) + S[2 * i]) % mod;
        C = (rol(C ^ u, t) + S[2 * i + 1]) % mod;

        [A, B, C, D] = [B, C, D, A];
    }

    A = (A + S[2 * r + 2]) % mod;
    C = (C + S[2 * r + 3]) % mod;

    const encrypted = new Uint32Array([A, B, C, D]);
    return toHex(new Uint8Array(encrypted.buffer)); // Convertir a hexadecimal
};

// Descifrado RC6
const rc6Decrypt = (ciphertext, S) => {
    const ciphertextBytes = Uint8Array.from(Buffer.from(ciphertext, 'hex')); // Convertir hex a bytes

    let A = new DataView(ciphertextBytes.buffer).getUint32(0);
    let B = new DataView(ciphertextBytes.buffer).getUint32(4);
    let C = new DataView(ciphertextBytes.buffer).getUint32(8);
    let D = new DataView(ciphertextBytes.buffer).getUint32(12);

    C = (C - S[2 * r + 3]) % mod;
    A = (A - S[2 * r + 2]) % mod;

    for (let i = r; i >= 1; i--) {
        [A, B, C, D] = [D, A, B, C];

        const t = rol((B * (2 * B + 1)) % mod, lgw);
        const u = rol((D * (2 * D + 1)) % mod, lgw);
        C = ror((C - S[2 * i + 1]) % mod, t) ^ u;
        A = ror((A - S[2 * i]) % mod, u) ^ t;
    }

    D = (D - S[1]) % mod;
    B = (B - S[0]) % mod;

    const decrypted = new Uint32Array([A, B, C, D]);
    return new TextDecoder().decode(new Uint8Array(decrypted.buffer)); // Convertir bytes a texto
};

// Función de Hash usando Blake2
const hashBlake2 = (str) => {
    const inputBytes = new TextEncoder().encode(str); // Convertir la cadena en bytes
    const hash = blake.blake2b(inputBytes); // Generar el hash usando blakejs
    return toHex(hash); // Convertir el resultado en formato hexadecimal
};

// Implementación de Rabin sin Buffer
const generateRabinKeys = () => {
    const p = 61;
    const q = 53;
    const n = p * q;
    return { p, q, n };
};

const rabinEncrypt = (message, n) => {
    const encoder = new TextEncoder();
    const messageBytes = encoder.encode(message);
    const m = parseInt(toHex(messageBytes), 16);
    return (m ** 2) % n;
};

const CifradoFormulario = () => {
    const [name, setName] = useState('');
    const [address, setAddress] = useState('');
    const [password, setPassword] = useState('');
    const [symmetricKey, setSymmetricKey] = useState('');
    const [publicKey, setPublicKey] = useState('');
    const [privateKey, setPrivateKey] = useState('');
    const [outputMessage, setOutputMessage] = useState('');

    const handleEncryption = () => {
        if (!name.trim() || !address.trim() || !password.trim()) {
            toast.error("Por favor ingrese todos los campos");
            return;
        }
        if (!symmetricKey.trim() || !publicKey.trim()) {
            toast.error("Por favor ingrese las claves");
            return;
        }

        // Ajustar la clave simétrica a 16 bytes
        const keyBytes = adjustKeyLength(symmetricKey);
        const S = generateKeySchedule(keyBytes);

        // Cifrar usando RC6
        const encryptedName = rc6Encrypt(name, S);
        const encryptedAddress = rc6Encrypt(address, S);

        // Cifrar usando Rabin
        const rabinKeys = generateRabinKeys();
        const encryptedPassword = rabinEncrypt(password, rabinKeys.n);

        // Hash para la contraseña
        const hashedPassword = hashBlake2(password);

        setOutputMessage(
            `Nombre cifrado (RC6): ${encryptedName}\n` +
            `Dirección cifrada (RC6): ${encryptedAddress}\n` +
            `Contraseña cifrada (Rabin): ${encryptedPassword}\n` +
            `Contraseña hashed (Blake2): ${hashedPassword}`
        );
    };

    const handleDecryption = () => {
        if (!symmetricKey.trim() || !privateKey.trim()) {
            toast.error("Por favor ingrese las claves");
            return;
        }

        try {
            const keyBytes = adjustKeyLength(symmetricKey); // Ajustar la clave a bytes
            const S = generateKeySchedule(keyBytes);

            const decryptedName = rc6Decrypt(outputMessage.name, S);
            const decryptedAddress = rc6Decrypt(outputMessage.address, S);

            setOutputMessage(
                `Nombre descifrado: ${decryptedName}\n` +
                `Dirección descifrada: ${decryptedAddress}`
            );
        } catch (error) {
            toast.error("Error al descifrar los datos");
        }
    };

    return (
        <div style={{ padding: "20px", textAlign: "center" }}>
            <h1>Formulario de Cifrado y Hash</h1>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Nombre"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Dirección"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="password"
                    placeholder="Contraseña"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Clave Simétrica (RC6)"
                    value={symmetricKey}
                    onChange={(e) => setSymmetricKey(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Clave Pública (Rabin)"
                    value={publicKey}
                    onChange={(e) => setPublicKey(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Clave Privada (Rabin)"
                    value={privateKey}
                    onChange={(e) => setPrivateKey(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <Button
                    onClick={handleEncryption}
                    variant="contained"
                    color="primary"
                    style={{ marginRight: "10px" }}
                >
                    Cifrar Datos
                </Button>
                <Button
                    onClick={handleDecryption}
                    variant="contained"
                    color="secondary"
                >
                    Descifrar Datos
                </Button>
            </div>
            <div>
                {outputMessage && (
                    <textarea
                        readOnly
                        value={outputMessage}
                        style={{
                            width: "300px",
                            height: "200px",
                            padding: "10px",
                            fontSize: "16px",
                            resize: "none",
                            border: "1px solid #ccc",
                            borderRadius: "4px",
                            marginBottom: "10px",
                        }}
                    />
                )}
            </div>
        </div>
    );
};

export { CifradoFormulario };
