import { useState } from "react";
import Button from '@mui/material/Button';
import toast from "react-hot-toast";
import blake from 'blakejs';

const CifradoFormulario = () => {
    const [name, setName] = useState('');
    const [address, setAddress] = useState('');
    const [password, setPassword] = useState('');
    const [symmetricKey, setSymmetricKey] = useState('');
    const [publicKey, setPublicKey] = useState('');
    const [privateKey, setPrivateKey] = useState('');
    const [outputMessage, setOutputMessage] = useState('');


    return (
        <div style={{ padding: "20px", textAlign: "center" }}>
            <h1>Formulario de Cifrado y Hash</h1>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Nombre"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Dirección"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="password"
                    placeholder="Contraseña"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Clave Simétrica (RC6)"
                    value={symmetricKey}
                    onChange={(e) => setSymmetricKey(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Clave Pública (Rabin)"
                    value={publicKey}
                    onChange={(e) => setPublicKey(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Clave Privada (Rabin)"
                    value={privateKey}
                    onChange={(e) => setPrivateKey(e.target.value)}
                    style={{ padding: "10px", width: "300px", fontSize: "16px" }}
                />
            </div>
            <div style={{ marginBottom: "20px" }}>
                <Button
                    variant="contained"
                    color="primary"
                    style={{ marginRight: "10px" }}
                >
                    Cifrar Datos
                </Button>
                <Button
                    variant="contained"
                    color="secondary"
                >
                    Descifrar Datos
                </Button>
            </div>
            <div>
                {outputMessage && (
                    <textarea
                        readOnly
                        value={outputMessage}
                        style={{
                            width: "300px",
                            height: "200px",
                            padding: "10px",
                            fontSize: "16px",
                            resize: "none",
                            border: "1px solid #ccc",
                            borderRadius: "4px",
                            marginBottom: "10px",
                        }}
                    />
                )}
            </div>
        </div>
    );
};

export { CifradoFormulario };
