                    <div style={{ marginBottom: "20px" }}>
                        <text>Direccion: {addressDecrypt}</text>
                    </div>
                    <div style={{ marginBottom: "20px" }}>
                        <text>Tarjeta Debito/Credito: {targetDebDecrypt}</text>
                    </div>
                </div>
            </div>

            {/* <div>
                {outputMessage && (
                    <textarea
                        readOnly
                        value={outputMessage}
